mapper.select:
 		
 		
		SELECT * FROM 
		(SELECT ROWNUM RN, TT.* FROM
		(SELECT * FROM ohmygoal_board ORDER BY SEQ DESC) TT 
		)WHERE RN >= #{startNum} AND RN <= #{endNum}

 		
 		
		SELECT * FROM 
		(SELECT ROWNUM RN, TT.* FROM
		(SELECT * FROM ohmygoal_member ORDER BY SEQ DESC) TT 
		)WHERE RN >= #{startNum} AND RN <= #{endNum}
